import React, { useState } from 'react';
import {
    Container,
    Typography,
    Box,
    Avatar,
    Button,
    TextField,
    Chip,
    Card,
    CardContent,
    Snackbar,
    Alert,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    MenuItem,
    Select,
    FormControl,
    InputLabel,
    IconButton,
} from '@mui/material';
import { Edit, Add, Delete } from '@mui/icons-material';

const ProfilePage = () => {
    const [profile, setProfile] = useState({
        name: '',
        location: '',
        hourlyRate: '',
        bio: '',
        skills: [],
        workHistory: [],
        education: [],
        profilePhoto: null,
        resume: null,
    });

    const [skillInput, setSkillInput] = useState('');
    const [alert, setAlert] = useState({ open: false, message: '', severity: 'success' });
    const [dialog, setDialog] = useState({ type: '', open: false, data: {} });
    const [editDialogOpen, setEditDialogOpen] = useState(false);

    // Add skill functionality
    const handleSkillAdd = (e) => {
        if (e.key === 'Enter' && skillInput.trim()) {
            e.preventDefault();
            if (!profile.skills.includes(skillInput.trim())) {
                setProfile((prev) => ({
                    ...prev,
                    skills: [...prev.skills, skillInput.trim()],
                }));
                setSkillInput('');
            } else {
                setAlert({ open: true, message: 'Skill already added.', severity: 'warning' });
            }
        }
    };

    const handleSkillRemove = (skill) => {
        setProfile((prev) => ({
            ...prev,
            skills: prev.skills.filter((s) => s !== skill),
        }));
    };

    const handleDialogOpen = (type) => {
        setDialog({ type, open: true, data: {} });
    };

    const handleDialogClose = () => {
        setDialog({ type: '', open: false, data: {} });
    };

    const handleDialogSubmit = () => {
        if (dialog.type === 'workHistory') {
            setProfile((prev) => ({
                ...prev,
                workHistory: [...prev.workHistory, dialog.data],
            }));
        } else if (dialog.type === 'education') {
            setProfile((prev) => ({
                ...prev,
                education: [...prev.education, dialog.data],
            }));
        }
        handleDialogClose();
    };

    const handleDialogChange = (field, value) => {
        setDialog((prev) => ({
            ...prev,
            data: { ...prev.data, [field]: value },
        }));
    };

    const handleEditDialogOpen = () => {
        setEditDialogOpen(true);
    };

    const handleEditDialogClose = () => {
        setEditDialogOpen(false);
    };

    const handleEditDialogSubmit = () => {
        setProfile((prev) => ({
            ...prev,
            name: dialog.data.name || prev.name,
            location: dialog.data.location || prev.location,
            hourlyRate: dialog.data.hourlyRate || prev.hourlyRate,
        }));
        handleEditDialogClose();
    };

    const handleFileChange = (e, type) => {
        const file = e.target.files[0];
        if (file) {
            setProfile((prev) => ({ ...prev, [type]: file }));
        }
    };

    return (
        <Box
            sx={{
                backgroundColor: '#E3F2FD', // Light blue background
                minHeight: '100vh', // Full viewport height
                paddingTop: '80px', // Adjust for navbar height
            }}
        >
            <Container maxWidth="md">
                <Box sx={{ textAlign: 'center', mb: 3 }}>
                    <Typography
                        variant="h5"
                        sx={{
                            fontWeight: 'bold',
                            color: '#0D47A1', // Dark blue color
                        }}
                    >
                        Preview Profile
                    </Typography>
                    <Typography variant="body2" sx={{ mb: 2 }}>
                        Make any edits you want, then submit your profile. You can make more changes after it’s live.
                    </Typography>
                    <Button variant="contained" color="success">
                        Submit Profile
                    </Button>
                </Box>

                {/* Profile Information Section */}
                <Card sx={{ mb: 4, p: 3 }}>
                    <CardContent>
                        <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
                            <Avatar
                                sx={{ width: 100, height: 100, mr: 3 }}
                                src={
                                    profile.profilePhoto
                                        ? URL.createObjectURL(profile.profilePhoto)
                                        : 'https://via.placeholder.com/100'
                                }
                            />
                            <Box>
                                <Typography variant="h6">{profile.name || 'Your Name'}</Typography>
                                <Typography variant="body2">{profile.location || 'Your Location'}</Typography>
                                <Typography variant="body2">
                                    {profile.hourlyRate ? `$${profile.hourlyRate}.00 Hourly rate` : 'Set your hourly rate'}
                                </Typography>
                            </Box>
                            <IconButton color="primary" onClick={handleEditDialogOpen}>
                                <Edit />
                            </IconButton>
                        </Box>
                        <Typography variant="body1" sx={{ mb: 2 }}>
                            {profile.bio || 'Write a brief description about yourself...'}
                        </Typography>
                        <TextField
                            label="Bio"
                            fullWidth
                            multiline
                            rows={4}
                            value={profile.bio}
                            onChange={(e) => setProfile({ ...profile, bio: e.target.value })}
                        />
                        <Box sx={{ mt: 2 }}>
                            <Typography variant="h6">Upload Profile Photo</Typography>
                            <input type="file" accept="image/*" onChange={(e) => handleFileChange(e, 'profilePhoto')} />
                        </Box>
                        <Box sx={{ mt: 2 }}>
                            <Typography variant="h6">Upload Resume (PDF)</Typography>
                            <input type="file" accept="application/pdf" onChange={(e) => handleFileChange(e, 'resume')} />
                        </Box>
                    </CardContent>
                </Card>

                {/* Skills Section */}
                <Card sx={{ mb: 4, p: 3 }}>
                    <CardContent>
                        <Typography variant="h6" sx={{ mb: 2 }}>
                            Skills
                        </Typography>
                        <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1, mb: 2 }}>
                            {profile.skills.map((skill, index) => (
                                <Chip
                                    key={index}
                                    label={skill}
                                    onDelete={() => handleSkillRemove(skill)}
                                    color="primary"
                                />
                            ))}
                        </Box>
                        <TextField
                            label="Add a skill"
                            fullWidth
                            value={skillInput}
                            onChange={(e) => setSkillInput(e.target.value)}
                            onKeyPress={handleSkillAdd}
                            helperText="Press Enter to add a skill."
                        />
                    </CardContent>
                </Card>

                {/* Work History Section */}
                <Card sx={{ mb: 4, p: 3 }}>
                    <CardContent>
                        <Typography variant="h6" sx={{ mb: 2 }}>
                            Work History
                        </Typography>
                        {profile.workHistory.map((work, index) => (
                            <Box key={index} sx={{ mb: 2, display: 'flex', justifyContent: 'space-between' }}>
                                <Box>
                                    <Typography variant="subtitle1" sx={{ fontWeight: 'bold' }}>
                                        {work.company} | {work.position}
                                    </Typography>
                                    <Typography variant="body2">{work.duration}</Typography>
                                    <Typography variant="body2" sx={{ mb: 1 }}>
                                        {work.description}
                                    </Typography>
                                </Box>
                                <IconButton color="error" onClick={() => setProfile((prev) => ({
                                    ...prev,
                                    workHistory: prev.workHistory.filter((_, i) => i !== index),
                                }))}>
                                    <Delete />
                                </IconButton>
                            </Box>
                        ))}
                        <Button
                            variant="outlined"
                            color="primary"
                            startIcon={<Add />}
                            sx={{ mt: 2 }}
                            onClick={() => handleDialogOpen('workHistory')}
                        >
                            Add Work Experience
                        </Button>
                    </CardContent>
                </Card>

                {/* Education Section */}
                <Card sx={{ mb: 4, p: 3 }}>
                    <CardContent>
                        <Typography variant="h6" sx={{ mb: 2 }}>
                            Education
                        </Typography>
                        {profile.education.map((edu, index) => (
                            <Box key={index} sx={{ mb: 2, display: 'flex', justifyContent: 'space-between' }}>
                                <Box>
                                    <Typography variant="subtitle1" sx={{ fontWeight: 'bold' }}>
                                        {edu.school}
                                    </Typography>
                                    <Typography variant="body2">{edu.degree}</Typography>
                                </Box>
                                <IconButton color="error" onClick={() => setProfile((prev) => ({
                                    ...prev,
                                    education: prev.education.filter((_, i) => i !== index),
                                }))}>
                                    <Delete />
                                </IconButton>
                            </Box>
                        ))}
                        <Button
                            variant="outlined"
                            color="primary"
                            startIcon={<Add />}
                            sx={{ mt: 2 }}
                            onClick={() => handleDialogOpen('education')}
                        >
                            Add Education
                        </Button>
                    </CardContent>
                </Card>

                {/* Dialog for Adding Work History or Education */}
                <Dialog open={dialog.open} onClose={handleDialogClose}>
                    <DialogTitle>
                        {dialog.type === 'workHistory' ? 'Add Work Experience' : 'Add Education'}
                    </DialogTitle>
                    <DialogContent>
                        {dialog.type === 'workHistory' ? (
                            <>
                                <TextField
                                    label="Company"
                                    fullWidth
                                    margin="normal"
                                    onChange={(e) => handleDialogChange('company', e.target.value)}
                                />
                                <TextField
                                    label="Position"
                                    fullWidth
                                    margin="normal"
                                    onChange={(e) => handleDialogChange('position', e.target.value)}
                                />
                                <TextField
                                    label="Duration"
                                    fullWidth
                                    margin="normal"
                                    onChange={(e) => handleDialogChange('duration', e.target.value)}
                                />
                                <TextField
                                    label="Description"
                                    fullWidth
                                    multiline
                                    rows={3}
                                    margin="normal"
                                    onChange={(e) => handleDialogChange('description', e.target.value)}
                                />
                            </>
                        ) : (
                            <>
                                <TextField
                                    label="School"
                                    fullWidth
                                    margin="normal"
                                    onChange={(e) => handleDialogChange('school', e.target.value)}
                                />
                                <FormControl fullWidth margin="normal">
                                    <InputLabel>Degree</InputLabel>
                                    <Select
                                        value={dialog.data.degree || ''}
                                        onChange={(e) => handleDialogChange('degree', e.target.value)}
                                    >
                                        <MenuItem value="High School">High School</MenuItem>
                                        <MenuItem value="Bachelors">Bachelors</MenuItem>
                                        <MenuItem value="Masters">Masters</MenuItem>
                                        <MenuItem value="PhD">PhD</MenuItem>
                                        <MenuItem value="Certification Course">Certification Course</MenuItem>
                                    </Select>
                                </FormControl>
                            </>
                        )}
                    </DialogContent>
                    <DialogActions>
                        <Button onClick={handleDialogClose} color="secondary">
                            Cancel
                        </Button>
                        <Button onClick={handleDialogSubmit} color="primary">
                            Add
                        </Button>
                    </DialogActions>
                </Dialog>

                {/* Snackbar for Feedback */}
                <Snackbar
                    open={alert.open}
                    autoHideDuration={4000}
                    onClose={() => setAlert({ open: false })}
                    anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
                >
                    <Alert severity={alert.severity}>{alert.message}</Alert>
                </Snackbar>
            </Container>
        </Box>
    );
};

export default ProfilePage;
