import React, { useState } from 'react';
import {
    Container,
    Typography,
    Box,
    Avatar,
    Button,
    TextField,
    Chip,
    Card,
    CardContent,
    Divider,
    Snackbar,
    Alert,
} from '@mui/material';

const ProfilePage = () => {
    const [profile, setProfile] = useState({
        name: '',
        location: '',
        hourlyRate: '',
        bio: '',
        skills: [],
        workHistory: [],
        education: [],
        profilePhoto: null,
    });

    const [skillInput, setSkillInput] = useState('');
    const [alert, setAlert] = useState({ open: false, message: '', severity: 'success' });

    const handleSkillAdd = (e) => {
        if (e.key === 'Enter' && skillInput.trim()) {
            e.preventDefault();
            if (!profile.skills.includes(skillInput.trim())) {
                setProfile((prev) => ({
                    ...prev,
                    skills: [...prev.skills, skillInput.trim()],
                }));
                setSkillInput('');
            } else {
                setAlert({
                    open: true,
                    message: 'Skill already added.',
                    severity: 'warning',
                });
            }
        }
    };

    const handleSkillRemove = (skill) => {
        setProfile((prev) => ({
            ...prev,
            skills: prev.skills.filter((s) => s !== skill),
        }));
    };

    const handleFileChange = (e) => {
        setProfile((prev) => ({
            ...prev,
            profilePhoto: e.target.files[0],
        }));
    };

    const handleSubmitProfile = async () => {
        try {
            const formData = new FormData();
            formData.append('name', profile.name);
            formData.append('location', profile.location);
            formData.append('hourlyRate', profile.hourlyRate);
            formData.append('bio', profile.bio);
            formData.append('skills', JSON.stringify(profile.skills)); // Convert array to string
            formData.append('workHistory', JSON.stringify(profile.workHistory));
            formData.append('education', JSON.stringify(profile.education));

            if (profile.profilePhoto) {
                formData.append('profilePhoto', profile.profilePhoto);
            }

            const response = await fetch('/api/freelancers', {
                method: 'POST',
                body: formData,
            });

            const result = await response.json();

            if (result.success) {
                setAlert({ open: true, message: 'Profile submitted successfully!', severity: 'success' });
            } else {
                throw new Error(result.message);
            }
        } catch (error) {
            setAlert({ open: true, message: 'Error submitting profile', severity: 'error' });
            console.error(error);
        }
    };

    return (
        <Container maxWidth="md" sx={{ mt: 5, mb: 5 }}>
            {/* Profile Form */}
            <Card sx={{ mb: 4, p: 3 }}>
                <CardContent>
                    <Typography variant="h6">Profile Details</Typography>
                    <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, mt: 2 }}>
                        <TextField
                            label="Name"
                            fullWidth
                            value={profile.name}
                            onChange={(e) => setProfile({ ...profile, name: e.target.value })}
                        />
                        <TextField
                            label="Location"
                            fullWidth
                            value={profile.location}
                            onChange={(e) => setProfile({ ...profile, location: e.target.value })}
                        />
                        <TextField
                            label="Hourly Rate"
                            fullWidth
                            type="number"
                            value={profile.hourlyRate}
                            onChange={(e) => setProfile({ ...profile, hourlyRate: e.target.value })}
                        />
                        <TextField
                            label="Bio"
                            fullWidth
                            multiline
                            rows={4}
                            value={profile.bio}
                            onChange={(e) => setProfile({ ...profile, bio: e.target.value })}
                        />
                        <Box>
                            <Typography>Skills</Typography>
                            <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1, mb: 2 }}>
                                {profile.skills.map((skill, index) => (
                                    <Chip
                                        key={index}
                                        label={skill}
                                        onDelete={() => handleSkillRemove(skill)}
                                        color="primary"
                                    />
                                ))}
                            </Box>
                            <TextField
                                label="Add Skill"
                                fullWidth
                                value={skillInput}
                                onChange={(e) => setSkillInput(e.target.value)}
                                onKeyPress={handleSkillAdd}
                            />
                        </Box>
                        <Box>
                            <Typography>Profile Photo</Typography>
                            <input type="file" onChange={handleFileChange} />
                        </Box>
                        <Button variant="contained" color="success" onClick={handleSubmitProfile}>
                            Submit Profile
                        </Button>
                    </Box>
                </CardContent>
            </Card>
            {/* Snackbar */}
            <Snackbar
                open={alert.open}
                autoHideDuration={4000}
                onClose={() => setAlert({ open: false })}
                anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
            >
                <Alert severity={alert.severity}>{alert.message}</Alert>
            </Snackbar>
        </Container>
    );
};

export default ProfilePage;
