import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import Button from '@mui/material/Button';
import Avatar from '@mui/material/Avatar';
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';
import Drawer from '@mui/material/Drawer';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemText from '@mui/material/ListItemText';
import Box from '@mui/material/Box';
import apiClient from '../api/apiClient';
import logo from '../logo.svg'; // Import the logo

const Navbar = () => {
    const [profilePhoto, setProfilePhoto] = useState(null);
    const [mobileOpen, setMobileOpen] = useState(false); // State to handle drawer

    useEffect(() => {
        const fetchProfilePhoto = async () => {
            try {
                const response = await apiClient.get('/freelancers/profile', {
                    headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
                });
                setProfilePhoto(response.data.data.profilePhoto);
            } catch (error) {
                console.error(error);
            }
        };
        fetchProfilePhoto();
    }, []);

    const handleDrawerToggle = () => {
        setMobileOpen(!mobileOpen); // Toggle drawer state
    };

    const drawer = (
        <List>
            <ListItem button component={Link} to="/" onClick={() => setMobileOpen(false)}>
                <ListItemText primary="Home" />
            </ListItem>
            <ListItem button component={Link} to="/dashboard" onClick={() => setMobileOpen(false)}>
                <ListItemText primary="Opportunities" />
            </ListItem>
            <ListItem button component={Link} to="/profile" onClick={() => setMobileOpen(false)}>
                <ListItemText primary="Profile" />
            </ListItem>
        </List>
    );

    return (
        <>
            <AppBar
                position="fixed"
                sx={{
                    backgroundColor: 'rgba(255, 255, 255, 0.6)', // Slightly more transparent
                    backdropFilter: 'blur(12px)', // Enhance blur effect
                    boxShadow: 'none', // Remove default shadow
                    zIndex: 10, // Ensure navbar stays above other content
                }}
            >
                <Toolbar sx={{ display: 'flex', justifyContent: 'space-between' }}>
                    {/* Logo */}
                    <Box
                        component="img"
                        src={logo}
                        alt="Logo"
                        sx={{
                            height: 40,
                            cursor: 'pointer',
                            transition: 'transform 0.3s ease',
                            '&:hover': {
                                transform: 'scale(1.1)', // Hover effect
                            },
                        }}
                        onClick={() => (window.location.href = '/')} // Click redirects to home
                    />

                    {/* Desktop Navigation Links */}
                    <Box sx={{ display: { xs: 'none', md: 'flex' }, alignItems: 'center' }}>
                        <Button
                            color="inherit"
                            component={Link}
                            to="/"
                            sx={{
                                color: '#000', // Text color for visibility on transparent navbar
                                fontWeight: 'bold',
                                mx: 1,
                                '&:hover': {
                                    backgroundColor: 'rgba(0, 0, 0, 0.1)',
                                },
                            }}
                        >
                            Home
                        </Button>
                        <Button
                            color="inherit"
                            component={Link}
                            to="/dashboard"
                            sx={{
                                color: '#000',
                                fontWeight: 'bold',
                                mx: 1,
                                '&:hover': {
                                    backgroundColor: 'rgba(0, 0, 0, 0.1)',
                                },
                            }}
                        >
                            Opportunities
                        </Button>
                        <Button
                            color="inherit"
                            component={Link}
                            to="/profile"
                            sx={{
                                color: '#000',
                                fontWeight: 'bold',
                                mx: 1,
                                '&:hover': {
                                    backgroundColor: 'rgba(0, 0, 0, 0.1)',
                                },
                            }}
                        >
                            Profile
                        </Button>
                        <Avatar
                            alt="Profile Photo"
                            src={profilePhoto}
                            sx={{
                                ml: 2,
                                transition: 'transform 0.3s ease',
                                '&:hover': {
                                    transform: 'scale(1.1)',
                                },
                            }}
                        />
                    </Box>

                    {/* Hamburger Menu for Mobile */}
                    <IconButton
                        color="inherit"
                        edge="start"
                        aria-label="menu"
                        sx={{ display: { xs: 'block', md: 'none' }, color: '#000' }}
                        onClick={handleDrawerToggle}
                    >
                        <MenuIcon />
                    </IconButton>
                </Toolbar>
            </AppBar>

            {/* Drawer for Mobile Navigation */}
            <Drawer
                anchor="left"
                open={mobileOpen}
                onClose={handleDrawerToggle}
                sx={{ display: { xs: 'block', md: 'none' } }}
            >
                {drawer}
            </Drawer>
        </>
    );
};

export default Navbar;









