import React, { useState } from 'react';
import {
    Container,
    Typography,
    Box,
    Avatar,
    Button,
    TextField,
    Chip,
    Card,
    CardContent,
    Divider,
    Snackbar,
    Alert,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    MenuItem,
    Select,
    FormControl,
    InputLabel,
    IconButton,
} from '@mui/material';
import { Edit, Add, Delete } from '@mui/icons-material';

const ProfilePage = () => {
    const [profile, setProfile] = useState({
        name: '',
        location: '',
        hourlyRate: '',
        bio: '',
        skills: [],
        workHistory: [],
        education: [],
        profilePhoto: null,
    });

    const [skillInput, setSkillInput] = useState('');
    const [alert, setAlert] = useState({ open: false, message: '', severity: 'success' });
    const [dialog, setDialog] = useState({ type: '', open: false, data: {} });
    const [editDialogOpen, setEditDialogOpen] = useState(false); // For editing name, location, hourly rate
    const [editDialogData, setEditDialogData] = useState({ name: '', location: '', hourlyRate: '' }); // To manage edit dialog data

    // Add skill functionality
    const handleSkillAdd = (e) => {
        if (e.key === 'Enter' && skillInput.trim()) {
            e.preventDefault();
            if (!profile.skills.includes(skillInput.trim())) {
                setProfile((prev) => ({
                    ...prev,
                    skills: [...prev.skills, skillInput.trim()],
                }));
                setSkillInput('');
            } else {
                setAlert({ open: true, message: 'Skill already added.', severity: 'warning' });
            }
        }
    };

    // Remove skill
    const handleSkillRemove = (skill) => {
        setProfile((prev) => ({
            ...prev,
            skills: prev.skills.filter((s) => s !== skill),
        }));
    };

    // Handle Add Education/Work Experience Dialogs
    const handleDialogOpen = (type) => {
        setDialog({ type, open: true, data: {} });
    };

    const handleDialogClose = () => {
        setDialog({ type: '', open: false, data: {} });
    };

    const handleDialogSubmit = () => {
        if (dialog.type === 'workHistory') {
            setProfile((prev) => ({
                ...prev,
                workHistory: [...prev.workHistory, dialog.data],
            }));
        } else if (dialog.type === 'education') {
            setProfile((prev) => ({
                ...prev,
                education: [...prev.education, dialog.data],
            }));
        }
        handleDialogClose();
    };

    const handleDialogChange = (field, value) => {
        setDialog((prev) => ({
            ...prev,
            data: { ...prev.data, [field]: value },
        }));
    };

    // Open Edit Dialog
    const handleEditDialogOpen = () => {
        setEditDialogData({
            name: profile.name,
            location: profile.location,
            hourlyRate: profile.hourlyRate,
        });
        setEditDialogOpen(true);
    };

    // Close Edit Dialog
    const handleEditDialogClose = () => {
        setEditDialogOpen(false);
    };

    // Submit Edit Dialog
    const handleEditDialogSubmit = () => {
        setProfile((prev) => ({
            ...prev,
            name: editDialogData.name || prev.name,
            location: editDialogData.location || prev.location,
            hourlyRate: editDialogData.hourlyRate || prev.hourlyRate,
        }));
        setEditDialogOpen(false);
    };

    // Handle File Upload (Profile Photo)
    const handleFileChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            setProfile((prev) => ({ ...prev, profilePhoto: file }));
        }
    };

    // Validate Fields
    const validateFields = () => {
        if (
            !profile.name ||
            !profile.location ||
            !profile.hourlyRate ||
            !profile.bio ||
            profile.skills.length === 0 ||
            profile.workHistory.length === 0 ||
            profile.education.length === 0
        ) {
            setAlert({ open: true, message: 'Please complete all fields.', severity: 'warning' });
            return false;
        }
        return true;
    };

    // Submit Profile to Backend
    const handleSubmitProfile = async () => {
        if (!validateFields()) return;

        const formData = new FormData();
        formData.append('name', profile.name);
        formData.append('location', profile.location);
        formData.append('hourlyRate', profile.hourlyRate);
        formData.append('bio', profile.bio);
        formData.append('skills', JSON.stringify(profile.skills));
        formData.append('workHistory', JSON.stringify(profile.workHistory));
        formData.append('education', JSON.stringify(profile.education));

        if (profile.profilePhoto) {
            formData.append('profilePhoto', profile.profilePhoto);
        }

        try {
            const response = await fetch('/api/freelancers', {
                method: 'POST',
                body: formData,
            });

            const result = await response.json();

            if (result.success) {
                setAlert({ open: true, message: 'Profile submitted successfully!', severity: 'success' });
            } else {
                throw new Error(result.message);
            }
        } catch (error) {
            setAlert({ open: true, message: 'Error submitting profile.', severity: 'error' });
        }
    };

    return (
        <Container maxWidth="md" sx={{ mt: 5, mb: 5 }}>
            {/* Profile Submission Section */}
            <Box sx={{ textAlign: 'center', mb: 3 }}>
                <Typography variant="h5">Preview Profile</Typography>
                <Typography variant="body2" sx={{ mb: 2 }}>
                    Make any edits you want, then submit your profile. You can make more changes after itâ€™s live.
                </Typography>
                <Button variant="contained" color="success" onClick={handleSubmitProfile}>
                    Submit Profile
                </Button>
            </Box>

            {/* Profile Information Section */}
            <Card sx={{ mb: 4, p: 3 }}>
                <CardContent>
                    <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
                        <Avatar
                            sx={{ width: 100, height: 100, mr: 3 }}
                            src={profile.profilePhoto ? URL.createObjectURL(profile.profilePhoto) : 'https://via.placeholder.com/100'}
                        />
                        <Box>
                            <Typography variant="h6">{profile.name || 'Your Name'}</Typography>
                            <Typography variant="body2">{profile.location || 'Your Location'}</Typography>
                            <Typography variant="body2">
                                {profile.hourlyRate ? `$${profile.hourlyRate}/hr` : 'Set your hourly rate'}
                            </Typography>
                        </Box>
                        <IconButton color="primary" onClick={handleEditDialogOpen}>
                            <Edit />
                        </IconButton>
                    </Box>
                    <Typography variant="body1" sx={{ mb: 2 }}>
                        {profile.bio || 'Write a brief description about yourself...'}
                    </Typography>
                    <TextField
                        label="Bio"
                        fullWidth
                        multiline
                        rows={4}
                        value={profile.bio}
                        onChange={(e) => setProfile({ ...profile, bio: e.target.value })}
                    />
                    <Box sx={{ mt: 2 }}>
                        <Typography variant="h6">Upload Profile Photo</Typography>
                        <input type="file" accept="image/*" onChange={handleFileChange} />
                    </Box>
                </CardContent>
            </Card>

            {/* Edit Dialog */}
            <Dialog open={editDialogOpen} onClose={handleEditDialogClose}>
                <DialogTitle>Edit Profile Information</DialogTitle>
                <DialogContent>
                    <TextField
                        label="Name"
                        fullWidth
                        margin="normal"
                        value={editDialogData.name}
                        onChange={(e) => setEditDialogData({ ...editDialogData, name: e.target.value })}
                    />
                    <TextField
                        label="Location"
                        fullWidth
                        margin="normal"
                        value={editDialogData.location}
                        onChange={(e) => setEditDialogData({ ...editDialogData, location: e.target.value })}
                    />
                    <TextField
                        label="Hourly Rate ($/hr)"
                        fullWidth
                        margin="normal"
                        type="number"
                        value={editDialogData.hourlyRate}
                        onChange={(e) => setEditDialogData({ ...editDialogData, hourlyRate: e.target.value })}
                    />
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleEditDialogClose} color="secondary">
                        Cancel
                    </Button>
                    <Button onClick={handleEditDialogSubmit} color="primary">
                        Save
                    </Button>
                </DialogActions>
            </Dialog>

            {/* Skills Section */}
            {/* Work History Section */}
            {/* Education Section */}
            {/* Dialog for Adding Work History or Education */}
            {/* Snackbar for Feedback */}
        </Container>
    );
};

export default ProfilePage;
