import React, { useState, useEffect } from 'react';
import apiClient from '../api/apiClient';
import {
    Container,
    TextField,
    Button,
    Typography,
    Box,
    Avatar,
    Snackbar,
    Alert,
    Chip,
    FormControl,
    InputLabel,
    Select,
    MenuItem,
} from '@mui/material';

const Profile = () => {
    const [profile, setProfile] = useState({
        bio: '',
        skills: [],
        experience: '',
        resume: null,
        profilePhoto: null,
    });

    const [skillInput, setSkillInput] = useState('');
    const [profilePhotoPreview, setProfilePhotoPreview] = useState(null);
    const [alert, setAlert] = useState({ open: false, message: '', severity: 'success' });

    useEffect(() => {
        const fetchProfile = async () => {
            try {
                const response = await apiClient.get('/freelancers/profile', {
                    headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
                });
                setProfile(response.data.data);
                if (response.data.data.profilePhoto) {
                    setProfilePhotoPreview(response.data.data.profilePhoto);
                }
            } catch (error) {
                console.error(error);
            }
        };
        fetchProfile();
    }, []);

    const handleFileChange = (e) => {
        const { name, files } = e.target;
        setProfile({ ...profile, [name]: files[0] });

        if (name === 'profilePhoto') {
            const reader = new FileReader();
            reader.onload = () => {
                setProfilePhotoPreview(reader.result);
            };
            reader.readAsDataURL(files[0]);
        }
    };

    const handleSkillAdd = (e) => {
        if (e.key === 'Enter' && skillInput.trim()) {
            e.preventDefault();
            if (!profile.skills.includes(skillInput.trim())) {
                setProfile((prev) => ({
                    ...prev,
                    skills: [...prev.skills, skillInput.trim()],
                }));
                setSkillInput('');
            } else {
                setAlert({
                    open: true,
                    message: 'Skill already added.',
                    severity: 'warning',
                });
            }
        }
    };

    const handleSkillRemove = (skillToRemove) => {
        setProfile((prev) => ({
            ...prev,
            skills: prev.skills.filter((skill) => skill !== skillToRemove),
        }));
    };

    const validateProfile = () => {
        if (!profile.bio.trim() || profile.skills.length === 0 || !profile.experience) {
            setAlert({
                open: true,
                message: 'All fields are required.',
                severity: 'error',
            });
            return false;
        }

        if (!profile.resume || profile.resume.type !== 'application/pdf') {
            setAlert({
                open: true,
                message: 'Please upload a valid resume in PDF format.',
                severity: 'error',
            });
            return false;
        }

        if (
            !profile.profilePhoto ||
            !['image/jpeg', 'image/png'].includes(profile.profilePhoto.type)
        ) {
            setAlert({
                open: true,
                message: 'Please upload a valid profile photo in JPG or PNG format.',
                severity: 'error',
            });
            return false;
        }

        return true;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!validateProfile()) return;

        const formData = new FormData();
        formData.append('bio', profile.bio);
        formData.append('skills', JSON.stringify(profile.skills));
        formData.append('experience', profile.experience);
        formData.append('resume', profile.resume);
        formData.append('profilePhoto', profile.profilePhoto);

        try {
            await apiClient.put('/freelancers/profile', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                    Authorization: `Bearer ${localStorage.getItem('token')}`,
                },
            });
            setAlert({
                open: true,
                message: 'Profile updated successfully!',
                severity: 'success',
            });
        } catch (error) {
            setAlert({
                open: true,
                message: 'Error saving profile. Please try again.',
                severity: 'error',
            });
            console.error(error);
        }
    };

    const handleAlertClose = () => {
        setAlert({ ...alert, open: false });
    };

    return (
        <Container maxWidth="sm" sx={{ mt: 5 }}>
            <Typography variant="h4" align="center" gutterBottom>
                Profile
            </Typography>
            <Box align="center" sx={{ mb: 3 }}>
                <Avatar
                    alt="Profile Photo"
                    src={profilePhotoPreview}
                    sx={{ width: 100, height: 100, mb: 2 }}
                />
                <input
                    accept="image/*"
                    type="file"
                    name="profilePhoto"
                    onChange={handleFileChange}
                />
            </Box>
            <Box component="form" onSubmit={handleSubmit} noValidate sx={{ mt: 3 }}>
                <TextField
                    label="Bio"
                    fullWidth
                    multiline
                    rows={4}
                    margin="normal"
                    value={profile.bio}
                    onChange={(e) => setProfile({ ...profile, bio: e.target.value })}
                />
                <Box sx={{ mt: 2 }}>
                    <TextField
                        label="Add Skills"
                        fullWidth
                        margin="normal"
                        value={skillInput}
                        onChange={(e) => setSkillInput(e.target.value)}
                        onKeyPress={handleSkillAdd}
                        helperText="Press Enter to add a skill."
                    />
                    <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1, mt: 1 }}>
                        {profile.skills.map((skill, index) => (
                            <Chip
                                key={index}
                                label={skill}
                                onDelete={() => handleSkillRemove(skill)}
                                color="primary"
                            />
                        ))}
                    </Box>
                </Box>
                <FormControl fullWidth sx={{ mt: 2 }}>
                    <InputLabel id="experience-label">Experience</InputLabel>
                    <Select
                        labelId="experience-label"
                        id="experience"
                        value={profile.experience}
                        onChange={(e) => setProfile({ ...profile, experience: e.target.value })}
                        label="Experience"
                    >
                        <MenuItem value="0-1 years">0-1 years</MenuItem>
                        <MenuItem value="2-4 years">2-4 years</MenuItem>
                        <MenuItem value="4+ years">4+ years</MenuItem>
                    </Select>
                </FormControl>
                <Box sx={{ mt: 2 }}>
                    <Typography variant="body1" gutterBottom>
                        Upload Resume (PDF only)
                    </Typography>
                    <input
                        accept=".pdf"
                        type="file"
                        name="resume"
                        onChange={handleFileChange}
                    />
                </Box>
                <Button type="submit" variant="contained" color="primary" fullWidth sx={{ mt: 2 }}>
                    Save Profile
                </Button>
            </Box>
            <Snackbar
                open={alert.open}
                autoHideDuration={4000}
                onClose={handleAlertClose}
                anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
            >
                <Alert
                    onClose={handleAlertClose}
                    severity={alert.severity}
                    sx={{ width: '100%' }}
                >
                    {alert.message}
                </Alert>
            </Snackbar>
        </Container>
    );
};

export default Profile;
