import React, { useState } from 'react';
import {
    Container,
    Typography,
    Box,
    Avatar,
    Button,
    TextField,
    Chip,
    Card,
    CardContent,
    Grid,
    Divider,
    IconButton,
    Snackbar,
    Alert,
} from '@mui/material';
import { Edit, Add, Delete } from '@mui/icons-material';

const ProfilePage = () => {
    const [profile, setProfile] = useState({
        name: 'Bruce Wayne',
        location: 'Boston, MA',
        hourlyRate: 30,
        bio: "I'm a developer experienced in building websites for small and medium-sized businesses.",
        skills: ['Software', 'Java', 'Python'],
        workHistory: [
            {
                company: 'TechVizor',
                position: 'JAVA Developer Intern',
                duration: 'February 2023 - May 2023',
                description:
                    'Collaborated with a team of 5 developers to design and implement 3 major Java applications.',
            },
            {
                company: 'BrainyBeam Technologies',
                position: 'Data Science Intern',
                duration: 'June 2022 - August 2022',
                description:
                    'Processed and analyzed datasets, improving model accuracy by 20% and reducing errors by 15%.',
            },
        ],
        education: [
            {
                school: 'Northeastern University',
                degree: 'Master of Science in Information Technology (MScIT)',
            },
            {
                school: 'Gujarat Technological University',
                degree: 'Bachelor of Engineering (BEng) 2019-2023',
            },
        ],
    });

    const [skillInput, setSkillInput] = useState('');
    const [alert, setAlert] = useState({ open: false, message: '', severity: 'success' });

    const handleSkillAdd = (e) => {
        if (e.key === 'Enter' && skillInput.trim()) {
            e.preventDefault();
            if (!profile.skills.includes(skillInput.trim())) {
                setProfile((prev) => ({
                    ...prev,
                    skills: [...prev.skills, skillInput.trim()],
                }));
                setSkillInput('');
            } else {
                setAlert({
                    open: true,
                    message: 'Skill already added.',
                    severity: 'warning',
                });
            }
        }
    };

    const handleSkillRemove = (skill) => {
        setProfile((prev) => ({
            ...prev,
            skills: prev.skills.filter((s) => s !== skill),
        }));
    };

    const handleAlertClose = () => {
        setAlert({ ...alert, open: false });
    };

    return (
        <Container maxWidth="md" sx={{ mt: 5, mb: 5 }}>
            <Box sx={{ textAlign: 'center', mb: 3 }}>
                <Typography variant="h5">Looking good, {profile.name.split(' ')[0]}!</Typography>
                <Typography variant="body2" sx={{ mb: 2 }}>
                    Make any edits you want, then submit your profile. You can make more changes after it’s live.
                </Typography>
                <Button variant="contained" color="success">
                    Submit profile
                </Button>
            </Box>
            <Card sx={{ mb: 4, p: 3 }}>
                <CardContent>
                    <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
                        <Avatar
                            sx={{ width: 100, height: 100, mr: 3 }}
                            src="https://via.placeholder.com/100"
                        />
                        <Box>
                            <Typography variant="h6">{profile.name}</Typography>
                            <Typography variant="body2">{profile.location}</Typography>
                            <Typography variant="body2">{`$${profile.hourlyRate}.00 Hourly rate`}</Typography>
                        </Box>
                        <IconButton color="primary">
                            <Edit />
                        </IconButton>
                    </Box>
                    <Typography variant="body1" sx={{ mb: 2 }}>
                        {profile.bio}
                    </Typography>
                </CardContent>
            </Card>

            <Card sx={{ mb: 4, p: 3 }}>
                <CardContent>
                    <Typography variant="h6" sx={{ mb: 2 }}>
                        Skills
                    </Typography>
                    <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1, mb: 2 }}>
                        {profile.skills.map((skill, index) => (
                            <Chip
                                key={index}
                                label={skill}
                                onDelete={() => handleSkillRemove(skill)}
                                color="primary"
                            />
                        ))}
                    </Box>
                    <TextField
                        label="Add a skill"
                        fullWidth
                        value={skillInput}
                        onChange={(e) => setSkillInput(e.target.value)}
                        onKeyPress={handleSkillAdd}
                        helperText="Press Enter to add a skill."
                    />
                </CardContent>
            </Card>

            <Card sx={{ mb: 4, p: 3 }}>
                <CardContent>
                    <Typography variant="h6" sx={{ mb: 2 }}>
                        Work History
                    </Typography>
                    {profile.workHistory.map((work, index) => (
                        <Box key={index} sx={{ mb: 2 }}>
                            <Typography variant="subtitle1" sx={{ fontWeight: 'bold' }}>
                                {work.company} | {work.position}
                            </Typography>
                            <Typography variant="body2">{work.duration}</Typography>
                            <Typography variant="body2" sx={{ mb: 1 }}>
                                {work.description}
                            </Typography>
                            <Divider sx={{ mb: 2 }} />
                        </Box>
                    ))}
                    <Button
                        variant="outlined"
                        color="primary"
                        startIcon={<Add />}
                        sx={{ mt: 2 }}
                    >
                        Add Work Experience
                    </Button>
                </CardContent>
            </Card>

            <Card sx={{ mb: 4, p: 3 }}>
                <CardContent>
                    <Typography variant="h6" sx={{ mb: 2 }}>
                        Education
                    </Typography>
                    {profile.education.map((edu, index) => (
                        <Box key={index} sx={{ mb: 2 }}>
                            <Typography variant="subtitle1" sx={{ fontWeight: 'bold' }}>
                                {edu.school}
                            </Typography>
                            <Typography variant="body2">{edu.degree}</Typography>
                            <Divider sx={{ mb: 2 }} />
                        </Box>
                    ))}
                    <Button
                        variant="outlined"
                        color="primary"
                        startIcon={<Add />}
                        sx={{ mt: 2 }}
                    >
                        Add Education
                    </Button>
                </CardContent>
            </Card>
            <Snackbar
                open={alert.open}
                autoHideDuration={4000}
                onClose={handleAlertClose}
                anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
            >
                <Alert
                    onClose={handleAlertClose}
                    severity={alert.severity}
                    sx={{ width: '100%' }}
                >
                    {alert.message}
                </Alert>
            </Snackbar>
        </Container>
    );
};

export default ProfilePage;
