import React, { useState } from 'react';
import {
    Container,
    Typography,
    Box,
    Avatar,
    Button,
    TextField,
    Chip,
    Card,
    CardContent,
    Snackbar,
    Alert,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    IconButton,
    Slide,
} from '@mui/material';
import { Edit, Add, Delete } from '@mui/icons-material';

const ProfilePage = () => {
    const [profile, setProfile] = useState({
        name: '',
        location: '',
        hourlyRate: '',
        bio: '',
        skills: [],
        workHistory: [],
        education: [],
        profilePhoto: null,
        resume: null,
    });

    const [skillInput, setSkillInput] = useState('');
    const [alert, setAlert] = useState({ open: false, message: '', severity: 'success' });
    const [dialog, setDialog] = useState({ open: false, type: '', data: {} });

    // Add Skill Functionality
    const handleSkillAdd = (e) => {
        if (e.key === 'Enter' && skillInput.trim()) {
            e.preventDefault();
            if (!profile.skills.includes(skillInput.trim())) {
                setProfile((prev) => ({
                    ...prev,
                    skills: [...prev.skills, skillInput.trim()],
                }));
                setSkillInput('');
            } else {
                setAlert({ open: true, message: 'Skill already added.', severity: 'warning' });
            }
        }
    };

    const handleSkillRemove = (skill) => {
        setProfile((prev) => ({
            ...prev,
            skills: prev.skills.filter((s) => s !== skill),
        }));
    };

    // Dialog for Editing Name, Location, and Hourly Rate
    const handleDialogOpen = () => {
        setDialog({
            open: true,
            type: 'editProfile',
            data: {
                name: profile.name,
                location: profile.location,
                hourlyRate: profile.hourlyRate,
            },
        });
    };

    const handleDialogClose = () => {
        setDialog({ open: false, type: '', data: {} });
    };

    const handleDialogSubmit = () => {
        if (dialog.type === 'editProfile') {
            const { name, location, hourlyRate } = dialog.data;
            setProfile((prev) => ({
                ...prev,
                name: name || prev.name,
                location: location || prev.location,
                hourlyRate: hourlyRate || prev.hourlyRate,
            }));
        }
        handleDialogClose();
    };

    const handleDialogChange = (field, value) => {
        setDialog((prev) => ({
            ...prev,
            data: { ...prev.data, [field]: value },
        }));
    };

    // Submit Profile to Backend
    const handleSubmitProfile = async () => {
        // Validation
        if (
            !profile.name ||
            !profile.location ||
            !profile.hourlyRate ||
            !profile.bio ||
            profile.skills.length === 0 ||
            profile.workHistory.length === 0 ||
            profile.education.length === 0 ||
            !profile.profilePhoto ||
            !profile.resume
        ) {
            setAlert({
                open: true,
                message: 'Please complete all required fields.',
                severity: 'error',
            });
            return;
        }

        // Submit to Backend
        const formData = new FormData();
        formData.append('name', profile.name);
        formData.append('location', profile.location);
        formData.append('hourlyRate', profile.hourlyRate);
        formData.append('bio', profile.bio);
        formData.append('skills', JSON.stringify(profile.skills));
        formData.append('workHistory', JSON.stringify(profile.workHistory));
        formData.append('education', JSON.stringify(profile.education));
        formData.append('profilePhoto', profile.profilePhoto);
        formData.append('resume', profile.resume);

        try {
            const response = await fetch('/api/freelancers', {
                method: 'POST',
                body: formData,
            });

            const result = await response.json();

            if (result.success) {
                setAlert({ open: true, message: 'Profile submitted successfully!', severity: 'success' });
            } else {
                throw new Error(result.message);
            }
        } catch (error) {
            setAlert({ open: true, message: 'Error submitting profile.', severity: 'error' });
        }
    };

    return (
        <Box
            sx={{
                backgroundColor: '#E3F2FD', // Light blue background
                minHeight: '100vh', // Full viewport height
                paddingTop: '80px', // Adjust for navbar height
            }}
        >
            <Container maxWidth="md">
                <Typography
                    variant="h5"
                    align="center"
                    sx={{ fontWeight: 'bold', color: '#0D47A1', mb: 3 }}
                >
                    Preview Profile
                </Typography>

                {/* Profile Information Section */}
                <Card sx={{ mb: 4, p: 3 }}>
                    <CardContent>
                        <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
                            <Avatar
                                sx={{ width: 100, height: 100, mr: 3 }}
                                src={
                                    profile.profilePhoto
                                        ? URL.createObjectURL(profile.profilePhoto)
                                        : 'https://via.placeholder.com/100'
                                }
                            />
                            <Box>
                                <Typography variant="h6">{profile.name || 'Your Name'}</Typography>
                                <Typography variant="body2">{profile.location || 'Your Location'}</Typography>
                                <Typography variant="body2">
                                    {profile.hourlyRate ? `$${profile.hourlyRate}.00 Hourly rate` : 'Set your hourly rate'}
                                </Typography>
                            </Box>
                            <IconButton color="primary" onClick={handleDialogOpen}>
                                <Edit />
                            </IconButton>
                        </Box>
                        <TextField
                            label="Bio"
                            fullWidth
                            multiline
                            rows={4}
                            value={profile.bio}
                            onChange={(e) => setProfile({ ...profile, bio: e.target.value })}
                            required
                        />
                        <Box sx={{ mt: 2 }}>
                            <Typography variant="h6">Upload Profile Photo</Typography>
                            <input
                                type="file"
                                accept="image/*"
                                onChange={(e) => setProfile({ ...profile, profilePhoto: e.target.files[0] })}
                                required
                            />
                        </Box>
                        <Box sx={{ mt: 2 }}>
                            <Typography variant="h6">Upload Resume (PDF)</Typography>
                            <input
                                type="file"
                                accept="application/pdf"
                                onChange={(e) => setProfile({ ...profile, resume: e.target.files[0] })}
                                required
                            />
                        </Box>
                    </CardContent>
                </Card>

                {/* Submit Button */}
                <Box sx={{ textAlign: 'center', mt: 4 }}>
                    <Button variant="contained" color="success" onClick={handleSubmitProfile}>
                        Submit Profile
                    </Button>
                </Box>

                {/* Snackbar for Alerts */}
                <Snackbar
                    open={alert.open}
                    autoHideDuration={4000}
                    onClose={() => setAlert({ open: false })}
                    anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
                    TransitionComponent={Slide}
                >
                    <Alert severity={alert.severity}>{alert.message}</Alert>
                </Snackbar>

                {/* Dialog for Editing Name, Location, and Hourly Rate */}
                <Dialog open={dialog.open} onClose={handleDialogClose}>
                    <DialogTitle>Edit Profile Information</DialogTitle>
                    <DialogContent>
                        <TextField
                            label="Name"
                            fullWidth
                            margin="normal"
                            value={dialog.data.name || ''}
                            onChange={(e) => handleDialogChange('name', e.target.value)}
                        />
                        <TextField
                            label="Location"
                            fullWidth
                            margin="normal"
                            value={dialog.data.location || ''}
                            onChange={(e) => handleDialogChange('location', e.target.value)}
                        />
                        <TextField
                            label="Hourly Rate"
                            type="number"
                            fullWidth
                            margin="normal"
                            value={dialog.data.hourlyRate || ''}
                            onChange={(e) => handleDialogChange('hourlyRate', e.target.value)}
                        />
                    </DialogContent>
                    <DialogActions>
                        <Button onClick={handleDialogClose} color="secondary">
                            Cancel
                        </Button>
                        <Button onClick={handleDialogSubmit} color="primary">
                            Save
                        </Button>
                    </DialogActions>
                </Dialog>
            </Container>
        </Box>
    );
};

export default ProfilePage;
