import React, { useState } from 'react';
import {
    Container,
    Typography,
    Box,
    Avatar,
    Button,
    TextField,
    Chip,
    Card,
    CardContent,
    Divider,
    Snackbar,
    Alert,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    MenuItem,
    Select,
    FormControl,
    InputLabel,
} from '@mui/material';
import { Edit, Add, Delete } from '@mui/icons-material';

const ProfilePage = () => {
    const [profile, setProfile] = useState({
        name: '',
        location: '',
        hourlyRate: '',
        bio: '',
        skills: [],
        workHistory: [],
        education: [],
        profilePhoto: null,
    });

    const [skillInput, setSkillInput] = useState('');
    const [alert, setAlert] = useState({ open: false, message: '', severity: 'success' });
    const [dialog, setDialog] = useState({ type: '', open: false, data: {} });

    // Add skill functionality
    const handleSkillAdd = (e) => {
        if (e.key === 'Enter' && skillInput.trim()) {
            e.preventDefault();
            if (!profile.skills.includes(skillInput.trim())) {
                setProfile((prev) => ({
                    ...prev,
                    skills: [...prev.skills, skillInput.trim()],
                }));
                setSkillInput('');
            } else {
                setAlert({ open: true, message: 'Skill already added.', severity: 'warning' });
            }
        }
    };

    // Remove skill
    const handleSkillRemove = (skill) => {
        setProfile((prev) => ({
            ...prev,
            skills: prev.skills.filter((s) => s !== skill),
        }));
    };

    // Handle file upload (profile photo)
    const handleFileChange = (e) => {
        setProfile((prev) => ({
            ...prev,
            profilePhoto: e.target.files[0],
        }));
    };

    // Submit Profile to Backend
    const handleSubmitProfile = async () => {
        const formData = new FormData();
        formData.append('name', profile.name);
        formData.append('location', profile.location);
        formData.append('hourlyRate', profile.hourlyRate);
        formData.append('bio', profile.bio);
        formData.append('skills', JSON.stringify(profile.skills));
        formData.append('workHistory', JSON.stringify(profile.workHistory));
        formData.append('education', JSON.stringify(profile.education));

        if (profile.profilePhoto) {
            formData.append('profilePhoto', profile.profilePhoto);
        }

        try {
            const response = await fetch('/api/freelancers', {
                method: 'POST',
                body: formData,
            });

            const result = await response.json();

            if (result.success) {
                setAlert({ open: true, message: 'Profile submitted successfully!', severity: 'success' });
            } else {
                throw new Error(result.message);
            }
        } catch (error) {
            setAlert({ open: true, message: 'Error submitting profile.', severity: 'error' });
        }
    };

    // Render the Profile Page
    return (
        <Container maxWidth="md" sx={{ mt: 5, mb: 5 }}>
            {/* Profile Submission Section */}
            <Box sx={{ textAlign: 'center', mb: 3 }}>
                <Typography variant="h5">Preview Profile</Typography>
                <Typography variant="body2" sx={{ mb: 2 }}>
                    Make any edits you want, then submit your profile. You can make more changes after itâ€™s live.
                </Typography>
                <Button variant="contained" color="success" onClick={handleSubmitProfile}>
                    Submit Profile
                </Button>
            </Box>

            {/* Profile Information Section */}
            <Card sx={{ mb: 4, p: 3 }}>
                <CardContent>
                    <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
                        <Avatar
                            sx={{ width: 100, height: 100, mr: 3 }}
                            src={profile.profilePhoto ? URL.createObjectURL(profile.profilePhoto) : 'https://via.placeholder.com/100'}
                        />
                        <Box>
                            <Typography variant="h6">{profile.name || 'Your Name'}</Typography>
                            <Typography variant="body2">{profile.location || 'Your Location'}</Typography>
                            <Typography variant="body2">
                                {profile.hourlyRate ? `$${profile.hourlyRate}.00 Hourly rate` : 'Set your hourly rate'}
                            </Typography>
                        </Box>
                    </Box>
                    <TextField
                        label="Bio"
                        fullWidth
                        multiline
                        rows={4}
                        value={profile.bio}
                        onChange={(e) => setProfile({ ...profile, bio: e.target.value })}
                    />
                </CardContent>
            </Card>

            {/* Skills Section */}
            <Card sx={{ mb: 4, p: 3 }}>
                <CardContent>
                    <Typography variant="h6" sx={{ mb: 2 }}>
                        Skills
                    </Typography>
                    <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1, mb: 2 }}>
                        {profile.skills.map((skill, index) => (
                            <Chip
                                key={index}
                                label={skill}
                                onDelete={() => handleSkillRemove(skill)}
                                color="primary"
                            />
                        ))}
                    </Box>
                    <TextField
                        label="Add a skill"
                        fullWidth
                        value={skillInput}
                        onChange={(e) => setSkillInput(e.target.value)}
                        onKeyPress={handleSkillAdd}
                        helperText="Press Enter to add a skill."
                    />
                </CardContent>
            </Card>

            {/* Upload Profile Photo */}
            <Card sx={{ mb: 4, p: 3 }}>
                <CardContent>
                    <Typography variant="h6">Upload Profile Photo</Typography>
                    <input type="file" accept="image/*" onChange={handleFileChange} />
                </CardContent>
            </Card>

            {/* Snackbar for Feedback */}
            <Snackbar
                open={alert.open}
                autoHideDuration={4000}
                onClose={() => setAlert({ open: false })}
                anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
            >
                <Alert severity={alert.severity}>{alert.message}</Alert>
            </Snackbar>
        </Container>
    );
};

export default ProfilePage;
