import React, { useState } from 'react';
import {
    Container,
    Typography,
    Box,
    Avatar,
    Button,
    TextField,
    Chip,
    Card,
    CardContent,
    Snackbar,
    Alert,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    IconButton,
    MenuItem,
    Select,
    FormControl,
    InputLabel,
} from '@mui/material';
import { Edit, Add, Delete } from '@mui/icons-material';

const ProfilePage = () => {
    const [profile, setProfile] = useState({
        name: '',
        location: '',
        hourlyRate: '',
        bio: '',
        skills: [],
        workHistory: [],
        education: [],
        profilePhoto: null,
        resume: null,
    });

    const [skillInput, setSkillInput] = useState('');
    const [alert, setAlert] = useState({ open: false, message: '', severity: 'success' });
    const [dialog, setDialog] = useState({ open: false, type: '', data: {} });

    // Add Skill Functionality
    const handleSkillAdd = (e) => {
        if (e.key === 'Enter' && skillInput.trim()) {
            e.preventDefault();
            if (!profile.skills.includes(skillInput.trim())) {
                setProfile((prev) => ({
                    ...prev,
                    skills: [...prev.skills, skillInput.trim()],
                }));
                setSkillInput('');
            } else {
                setAlert({ open: true, message: 'Skill already added.', severity: 'warning' });
            }
        }
    };

    const handleSkillRemove = (skill) => {
        setProfile((prev) => ({
            ...prev,
            skills: prev.skills.filter((s) => s !== skill),
        }));
    };

    const handleDialogOpen = (type) => {
        setDialog({ open: true, type, data: {} });
    };

    const handleDialogClose = () => {
        setDialog({ open: false, type: '', data: {} });
    };

    const handleDialogSubmit = () => {
        if (dialog.type === 'workHistory') {
            setProfile((prev) => ({
                ...prev,
                workHistory: [...prev.workHistory, dialog.data],
            }));
        } else if (dialog.type === 'education') {
            setProfile((prev) => ({
                ...prev,
                education: [...prev.education, dialog.data],
            }));
        }
        handleDialogClose();
    };

    const handleDialogChange = (field, value) => {
        setDialog((prev) => ({
            ...prev,
            data: { ...prev.data, [field]: value },
        }));
    };

    const handleFileChange = (e, type) => {
        const file = e.target.files[0];
        if (file) {
            setProfile((prev) => ({ ...prev, [type]: file }));
        }
    };

    const validateFields = () => {
        if (
            !profile.name ||
            !profile.location ||
            !profile.hourlyRate ||
            !profile.bio ||
            profile.skills.length === 0 ||
            profile.workHistory.length === 0 ||
            profile.education.length === 0 ||
            !profile.profilePhoto ||
            !profile.resume
        ) {
            setAlert({
                open: true,
                message: 'Please complete all required fields.',
                severity: 'error',
            });
            return false;
        }
        return true;
    };

    const handleSubmitProfile = async () => {
        if (!validateFields()) return;

        const formData = new FormData();
        formData.append('name', profile.name);
        formData.append('location', profile.location);
        formData.append('hourlyRate', profile.hourlyRate);
        formData.append('bio', profile.bio);
        formData.append('skills', JSON.stringify(profile.skills));
        formData.append('workHistory', JSON.stringify(profile.workHistory));
        formData.append('education', JSON.stringify(profile.education));
        formData.append('profilePhoto', profile.profilePhoto);
        formData.append('resume', profile.resume);

        try {
            const response = await fetch('/api/freelancers', {
                method: 'POST',
                body: formData,
            });

            const result = await response.json();

            if (result.success) {
                setAlert({ open: true, message: 'Profile submitted successfully!', severity: 'success' });
            } else {
                throw new Error(result.message);
            }
        } catch (error) {
            setAlert({ open: true, message: 'Error submitting profile.', severity: 'error' });
        }
    };

    return (
        <Box
            sx={{
                backgroundColor: '#E3F2FD',
                minHeight: '100vh',
                paddingTop: '80px',
            }}
        >
            <Container maxWidth="md">
                <Typography
                    variant="h5"
                    align="center"
                    sx={{ fontWeight: 'bold', color: '#0D47A1', mb: 3 }}
                >
                    Preview Profile
                </Typography>

                {/* Profile Information Section */}
                <Card sx={{ mb: 4, p: 3 }}>
                    <CardContent>
                        <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
                            <Avatar
                                sx={{ width: 100, height: 100, mr: 3 }}
                                src={
                                    profile.profilePhoto
                                        ? URL.createObjectURL(profile.profilePhoto)
                                        : 'https://via.placeholder.com/100'
                                }
                            />
                            <Box>
                                <Typography variant="h6">{profile.name || 'Your Name'}</Typography>
                                <Typography variant="body2">{profile.location || 'Your Location'}</Typography>
                                <Typography variant="body2">
                                    {profile.hourlyRate ? `$${profile.hourlyRate}/hr` : 'Set your hourly rate'}
                                </Typography>
                            </Box>
                        </Box>
                        <TextField
                            label="Bio"
                            fullWidth
                            multiline
                            rows={4}
                            value={profile.bio}
                            onChange={(e) => setProfile({ ...profile, bio: e.target.value })}
                        />
                        <Box sx={{ mt: 2 }}>
                            <Typography variant="h6">Upload Profile Photo</Typography>
                            <input
                                type="file"
                                accept="image/*"
                                onChange={(e) => handleFileChange(e, 'profilePhoto')}
                                required
                            />
                        </Box>
                        <Box sx={{ mt: 2 }}>
                            <Typography variant="h6">Upload Resume (PDF)</Typography>
                            <input
                                type="file"
                                accept="application/pdf"
                                onChange={(e) => handleFileChange(e, 'resume')}
                                required
                            />
                        </Box>
                    </CardContent>
                </Card>

                {/* Skills Section */}
                <Card sx={{ mb: 4, p: 3 }}>
                    <CardContent>
                        <Typography variant="h6" sx={{ mb: 2 }}>
                            Skills
                        </Typography>
                        <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1, mb: 2 }}>
                            {profile.skills.map((skill, index) => (
                                <Chip
                                    key={index}
                                    label={skill}
                                    onDelete={() => handleSkillRemove(skill)}
                                    color="primary"
                                />
                            ))}
                        </Box>
                        <TextField
                            label="Add a skill"
                            fullWidth
                            value={skillInput}
                            onChange={(e) => setSkillInput(e.target.value)}
                            onKeyPress={handleSkillAdd}
                            helperText="Press Enter to add a skill."
                        />
                    </CardContent>
                </Card>

                {/* Work History Section */}
                <Card sx={{ mb: 4, p: 3 }}>
                    <CardContent>
                        <Typography variant="h6" sx={{ mb: 2 }}>
                            Work History
                        </Typography>
                        {profile.workHistory.map((work, index) => (
                            <Box key={index} sx={{ mb: 2, display: 'flex', justifyContent: 'space-between' }}>
                                <Box>
                                    <Typography variant="subtitle1">{work.company}</Typography>
                                    <Typography variant="body2">{work.position}</Typography>
                                    <Typography variant="body2">{work.duration}</Typography>
                                </Box>
                                <IconButton
                                    color="error"
                                    onClick={() =>
                                        setProfile((prev) => ({
                                            ...prev,
                                            workHistory: prev.workHistory.filter((_, i) => i !== index),
                                        }))
                                    }
                                >
                                    <Delete />
                                </IconButton>
                            </Box>
                        ))}
                        <Button
                            variant="outlined"
                            color="primary"
                            startIcon={<Add />}
                            onClick={() => handleDialogOpen('workHistory')}
                        >
                            Add Work Experience
                        </Button>
                    </CardContent>
                </Card>

                {/* Education Section */}
                <Card sx={{ mb: 4, p: 3 }}>
                    <CardContent>
                        <Typography variant="h6" sx={{ mb: 2 }}>
                            Education
                        </Typography>
                        {profile.education.map((edu, index) => (
                            <Box key={index} sx={{ mb: 2, display: 'flex', justifyContent: 'space-between' }}>
                                <Box>
                                    <Typography variant="subtitle1">{edu.school}</Typography>
                                    <Typography variant="body2">{edu.degree}</Typography>
                                </Box>
                                <IconButton
                                    color="error"
                                    onClick={() =>
                                        setProfile((prev) => ({
                                            ...prev,
                                            education: prev.education.filter((_, i) => i !== index),
                                        }))
                                    }
                                >
                                    <Delete />
                                </IconButton>
                            </Box>
                        ))}
                        <Button
                            variant="outlined"
                            color="primary"
                            startIcon={<Add />}
                            onClick={() => handleDialogOpen('education')}
                        >
                            Add Education
                        </Button>
                    </CardContent>
                </Card>

                <Box sx={{ textAlign: 'center', mt: 4 }}>
                    <Button variant="contained" color="success" onClick={handleSubmitProfile}>
                        Submit Profile
                    </Button>
                </Box>

                {/* Snackbar for Alerts */}
                <Snackbar
                    open={alert.open}
                    autoHideDuration={4000}
                    onClose={() => setAlert({ open: false })}
                >
                    <Alert severity={alert.severity}>{alert.message}</Alert>
                </Snackbar>

                {/* Dialog */}
                <Dialog open={dialog.open} onClose={handleDialogClose}>
                    <DialogTitle>
                        {dialog.type === 'workHistory' ? 'Add Work Experience' : 'Add Education'}
                    </DialogTitle>
                    <DialogContent>
                        {dialog.type === 'workHistory' ? (
                            <>
                                <TextField
                                    label="Company"
                                    fullWidth
                                    margin="normal"
                                    onChange={(e) => handleDialogChange('company', e.target.value)}
                                />
                                <TextField
                                    label="Position"
                                    fullWidth
                                    margin="normal"
                                    onChange={(e) => handleDialogChange('position', e.target.value)}
                                />
                                <TextField
                                    label="Duration"
                                    fullWidth
                                    margin="normal"
                                    onChange={(e) => handleDialogChange('duration', e.target.value)}
                                />
                            </>
                        ) : (
                            <>
                                <TextField
                                    label="School"
                                    fullWidth
                                    margin="normal"
                                    onChange={(e) => handleDialogChange('school', e.target.value)}
                                />
                                <FormControl fullWidth margin="normal">
                                    <InputLabel>Degree</InputLabel>
                                    <Select
                                        value={dialog.data.degree || ''}
                                        onChange={(e) => handleDialogChange('degree', e.target.value)}
                                    >
                                        <MenuItem value="High School">High School</MenuItem>
                                        <MenuItem value="Bachelors">Bachelors</MenuItem>
                                        <MenuItem value="Masters">Masters</MenuItem>
                                        <MenuItem value="PhD">PhD</MenuItem>
                                        <MenuItem value="Certification">Certification</MenuItem>
                                    </Select>
                                </FormControl>
                            </>
                        )}
                    </DialogContent>
                    <DialogActions>
                        <Button onClick={handleDialogClose} color="secondary">
                            Cancel
                        </Button>
                        <Button onClick={handleDialogSubmit} color="primary">
                            Add
                        </Button>
                    </DialogActions>
                </Dialog>
            </Container>
        </Box>
    );
};

export default ProfilePage;
