import React, { useState } from 'react';
import {
    Container,
    Typography,
    Box,
    Avatar,
    Button,
    TextField,
    Chip,
    Card,
    CardContent,
    Snackbar,
    Alert,
} from '@mui/material';

const ProfilePage = () => {
    const [profile, setProfile] = useState({
        name: '',
        location: '',
        hourlyRate: '',
        bio: '',
        jobRole: '',
        skills: [],
        profilePhoto: null,
        resume: null,
    });

    const [skillInput, setSkillInput] = useState('');
    const [alert, setAlert] = useState({ open: false, message: '', severity: 'success' });

    // Add Skill Functionality
    const handleSkillAdd = (e) => {
        if (e.key === 'Enter' && skillInput.trim()) {
            e.preventDefault();
            if (!profile.skills.includes(skillInput.trim())) {
                setProfile((prev) => ({
                    ...prev,
                    skills: [...prev.skills, skillInput.trim()],
                }));
                setSkillInput('');
            } else {
                setAlert({ open: true, message: 'Skill already added.', severity: 'warning' });
            }
        }
    };

    const handleSkillRemove = (skill) => {
        setProfile((prev) => ({
            ...prev,
            skills: prev.skills.filter((s) => s !== skill),
        }));
    };

    // File Upload Handlers
    const handleFileChange = (e, type) => {
        const file = e.target.files[0];
        if (file) {
            setProfile((prev) => ({ ...prev, [type]: file }));
        }
    };

    // Validation
    const validateFields = () => {
        if (
            !profile.name ||
            !profile.location ||
            !profile.hourlyRate ||
            !profile.bio ||
            !profile.jobRole ||
            profile.skills.length === 0 ||
            !profile.profilePhoto ||
            !profile.resume
        ) {
            setAlert({
                open: true,
                message: 'Please complete all required fields.',
                severity: 'error',
            });
            return false;
        }
        return true;
    };

    // Submit Data to DB
    const handleSubmitProfile = async () => {
        if (!validateFields()) return;

        const formData = new FormData();
        formData.append('name', profile.name);
        formData.append('location', profile.location);
        formData.append('hourlyRate', profile.hourlyRate);
        formData.append('bio', profile.bio);
        formData.append('jobRole', profile.jobRole);
        formData.append('skills', JSON.stringify(profile.skills));
        formData.append('profilePhoto', profile.profilePhoto);
        formData.append('resume', profile.resume);

        try {
            const response = await fetch('/api/freelancers', {
                method: 'POST',
                body: formData,
            });

            const result = await response.json();

            if (result.success) {
                setAlert({ open: true, message: 'Profile submitted successfully!', severity: 'success' });
            } else {
                throw new Error(result.message);
            }
        } catch (error) {
            setAlert({ open: true, message: 'Error submitting profile.', severity: 'error' });
        }
    };

    return (
        <Box
            sx={{
                backgroundColor: '#E3F2FD', // Light blue background
                minHeight: '100vh',
                paddingTop: '80px',
            }}
        >
            <Container maxWidth="sm">
                <Typography
                    variant="h5"
                    align="center"
                    sx={{ fontWeight: 'bold', color: '#0D47A1', mb: 3 }}
                >
                    Profile Details
                </Typography>

                <Card sx={{ p: 3, mb: 4, borderRadius: '16px' }}>
                    <CardContent>
                        {/* Profile Photo */}
                        <Box sx={{ textAlign: 'center', mb: 3 }}>
                            <Avatar
                                sx={{ width: 100, height: 100, mx: 'auto', mb: 2 }}
                                src={
                                    profile.profilePhoto
                                        ? URL.createObjectURL(profile.profilePhoto)
                                        : 'https://via.placeholder.com/100'
                                }
                            />
                            <Typography variant="body2" sx={{ mb: 1 }}>
                                Upload Profile Photo
                            </Typography>
                            <input
                                type="file"
                                accept="image/png, image/jpeg"
                                onChange={(e) => handleFileChange(e, 'profilePhoto')}
                            />
                        </Box>

                        {/* Basic Information */}
                        <TextField
                            label="Name"
                            fullWidth
                            required
                            value={profile.name}
                            onChange={(e) => setProfile({ ...profile, name: e.target.value })}
                            sx={{ mb: 2, borderRadius: '16px' }}
                        />
                        <TextField
                            label="Location"
                            fullWidth
                            required
                            value={profile.location}
                            onChange={(e) => setProfile({ ...profile, location: e.target.value })}
                            sx={{ mb: 2, borderRadius: '16px' }}
                        />
                        <TextField
                            label="Hourly Rate ($)"
                            fullWidth
                            required
                            type="number"
                            value={profile.hourlyRate}
                            onChange={(e) => setProfile({ ...profile, hourlyRate: e.target.value })}
                            sx={{ mb: 2, borderRadius: '16px' }}
                        />

                        {/* Bio Section */}
                        <TextField
                            label="Bio"
                            fullWidth
                            required
                            multiline
                            rows={4}
                            value={profile.bio}
                            onChange={(e) => setProfile({ ...profile, bio: e.target.value })}
                            sx={{ mb: 2, borderRadius: '16px' }}
                        />

                        {/* Job Role Section */}
                        <TextField
                            label="Job Role (e.g., Developer, Designer)"
                            fullWidth
                            required
                            value={profile.jobRole}
                            onChange={(e) => setProfile({ ...profile, jobRole: e.target.value })}
                            sx={{ mb: 2, borderRadius: '16px' }}
                        />

                        {/* Skills Section */}
                        <Typography variant="h6" sx={{ mb: 1 }}>
                            Skills
                        </Typography>
                        <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1, mb: 2 }}>
                            {profile.skills.map((skill, index) => (
                                <Chip
                                    key={index}
                                    label={skill}
                                    onDelete={() => handleSkillRemove(skill)}
                                    color="primary"
                                />
                            ))}
                        </Box>
                        <TextField
                            label="Add a skill"
                            fullWidth
                            value={skillInput}
                            onChange={(e) => setSkillInput(e.target.value)}
                            onKeyPress={handleSkillAdd}
                            helperText="Press Enter to add a skill."
                            sx={{ borderRadius: '16px' }}
                        />

                        {/* Resume Upload */}
                        <Box sx={{ mt: 3 }}>
                            <Typography variant="body2" sx={{ mb: 1 }}>
                                Upload Resume (PDF)
                            </Typography>
                            <input
                                type="file"
                                accept="application/pdf"
                                onChange={(e) => handleFileChange(e, 'resume')}
                                required
                            />
                        </Box>
                    </CardContent>
                </Card>

                {/* Submit Button */}
                <Box sx={{ textAlign: 'center' }}>
                    <Button
                        variant="contained"
                        color="success"
                        onClick={handleSubmitProfile}
                        sx={{ borderRadius: '16px', px: 4 }}
                    >
                        Submit Profile
                    </Button>
                </Box>

                {/* Snackbar for Alerts */}
                <Snackbar
                    open={alert.open}
                    autoHideDuration={4000}
                    onClose={() => setAlert({ open: false })}
                    anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
                >
                    <Alert severity={alert.severity} sx={{ width: '100%' }}>
                        {alert.message}
                    </Alert>
                </Snackbar>
            </Container>
        </Box>
    );
};

export default ProfilePage;
