import React, { useState, useEffect } from 'react';
import apiClient from '../api/apiClient';
import {
    Container,
    TextField,
    Button,
    Typography,
    Box,
    Avatar,
    Snackbar,
    Alert,
} from '@mui/material';

const Profile = () => {
    const [profile, setProfile] = useState({
        bio: '',
        skills: '',
        experience: '',
        resume: null,
        profilePhoto: null,
    });

    const [profilePhotoPreview, setProfilePhotoPreview] = useState(null);
    const [alert, setAlert] = useState({ open: false, message: '', severity: 'success' });

    useEffect(() => {
        const fetchProfile = async () => {
            try {
                const response = await apiClient.get('/freelancers/profile', {
                    headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
                });
                setProfile(response.data.data);
                if (response.data.data.profilePhoto) {
                    setProfilePhotoPreview(response.data.data.profilePhoto);
                }
            } catch (error) {
                console.error(error);
            }
        };
        fetchProfile();
    }, []);

    const handleFileChange = (e) => {
        const { name, files } = e.target;
        setProfile({ ...profile, [name]: files[0] });

        if (name === 'profilePhoto') {
            const reader = new FileReader();
            reader.onload = () => {
                setProfilePhotoPreview(reader.result);
            };
            reader.readAsDataURL(files[0]);
        }
    };

    const validateProfile = () => {
        if (!profile.bio.trim() || !profile.skills.trim() || !profile.experience.trim()) {
            setAlert({
                open: true,
                message: 'All text fields are required.',
                severity: 'error',
            });
            return false;
        }

        if (!profile.resume || profile.resume.type !== 'application/pdf') {
            setAlert({
                open: true,
                message: 'Please upload a valid resume in PDF format.',
                severity: 'error',
            });
            return false;
        }

        if (
            !profile.profilePhoto ||
            !['image/jpeg', 'image/png'].includes(profile.profilePhoto.type)
        ) {
            setAlert({
                open: true,
                message: 'Please upload a valid profile photo in JPG or PNG format.',
                severity: 'error',
            });
            return false;
        }

        return true;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!validateProfile()) return;

        const formData = new FormData();
        formData.append('bio', profile.bio);
        formData.append('skills', profile.skills);
        formData.append('experience', profile.experience);
        formData.append('resume', profile.resume);
        formData.append('profilePhoto', profile.profilePhoto);

        try {
            await apiClient.put('/freelancers/profile', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                    Authorization: `Bearer ${localStorage.getItem('token')}`,
                },
            });
            setAlert({
                open: true,
                message: 'Profile updated successfully!',
                severity: 'success',
            });
        } catch (error) {
            setAlert({
                open: true,
                message: 'Error saving profile. Please try again.',
                severity: 'error',
            });
            console.error(error);
        }
    };

    const handleAlertClose = () => {
        setAlert({ ...alert, open: false });
    };

    return (
        <Container maxWidth="sm" sx={{ mt: 5 }}>
            <Typography variant="h4" align="center" gutterBottom>
                Profile
            </Typography>
            <Box align="center" sx={{ mb: 3 }}>
                <Avatar
                    alt="Profile Photo"
                    src={profilePhotoPreview}
                    sx={{ width: 100, height: 100, mb: 2 }}
                />
                <input
                    accept="image/*"
                    type="file"
                    name="profilePhoto"
                    onChange={handleFileChange}
                />
            </Box>
            <Box component="form" onSubmit={handleSubmit} noValidate sx={{ mt: 3 }}>
                <TextField
                    label="Bio"
                    fullWidth
                    multiline
                    rows={4}
                    margin="normal"
                    value={profile.bio}
                    onChange={(e) => setProfile({ ...profile, bio: e.target.value })}
                />
                <TextField
                    label="Skills"
                    fullWidth
                    margin="normal"
                    value={profile.skills}
                    onChange={(e) => setProfile({ ...profile, skills: e.target.value })}
                />
                <TextField
                    label="Experience"
                    fullWidth
                    multiline
                    rows={4}
                    margin="normal"
                    value={profile.experience}
                    onChange={(e) => setProfile({ ...profile, experience: e.target.value })}
                />
                <Box sx={{ mt: 2 }}>
                    <Typography variant="body1" gutterBottom>
                        Upload Resume (PDF only)
                    </Typography>
                    <input
                        accept=".pdf"
                        type="file"
                        name="resume"
                        onChange={handleFileChange}
                    />
                </Box>
                <Button type="submit" variant="contained" color="primary" fullWidth sx={{ mt: 2 }}>
                    Save Profile
                </Button>
            </Box>
            <Snackbar
                open={alert.open}
                autoHideDuration={4000}
                onClose={handleAlertClose}
                anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
            >
                <Alert
                    onClose={handleAlertClose}
                    severity={alert.severity}
                    sx={{ width: '100%' }}
                >
                    {alert.message}
                </Alert>
            </Snackbar>
        </Container>
    );
};

export default Profile;
