import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import Avatar from '@mui/material/Avatar';
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';
import Drawer from '@mui/material/Drawer';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemText from '@mui/material/ListItemText';
import Box from '@mui/material/Box';
import apiClient from '../api/apiClient';
import logo from '../logo.svg'; // Import the logo

const Navbar = () => {
    const [profilePhoto, setProfilePhoto] = useState(null);
    const [mobileOpen, setMobileOpen] = useState(false); // State to handle drawer

    useEffect(() => {
        const fetchProfilePhoto = async () => {
            try {
                const response = await apiClient.get('/freelancers/profile', {
                    headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
                });
                setProfilePhoto(response.data.data.profilePhoto);
            } catch (error) {
                console.error(error);
            }
        };
        fetchProfilePhoto();
    }, []);

    const handleDrawerToggle = () => {
        setMobileOpen(!mobileOpen); // Toggle drawer state
    };

    const drawer = (
        <List>
            <ListItem button component={Link} to="/" onClick={() => setMobileOpen(false)}>
                <ListItemText primary="Home" />
            </ListItem>
            <ListItem button component={Link} to="/dashboard" onClick={() => setMobileOpen(false)}>
                <ListItemText primary="Opportunities" />
            </ListItem>
            <ListItem button component={Link} to="/profile" onClick={() => setMobileOpen(false)}>
                <ListItemText primary="Profile" />
            </ListItem>
        </List>
    );

    return (
        <>
            <AppBar
                position="static"
                sx={{
                    background: 'linear-gradient(90deg, #1976d2, #42a5f5)', // Gradient background
                    borderRadius: '8px', // Border-radius for a modern look
                    margin: '8px', // Margins for spacing
                    boxShadow: '0px 4px 10px rgba(0, 0, 0, 0.2)', // Shadow for depth
                }}
            >
                <Toolbar>
                    {/* Logo and Title */}
                    <Box sx={{ display: 'flex', alignItems: 'center', flexGrow: 1 }}>
                        <Box
                            component="img"
                            src={logo}
                            alt="Logo"
                            sx={{
                                width: 40,
                                height: 40,
                                mr: 1,
                                transition: 'transform 0.3s ease',
                                '&:hover': {
                                    transform: 'scale(1.2)',
                                },
                            }}
                        />
                        <Typography
                            variant="h6"
                            sx={{
                                fontWeight: 'bold',
                                color: '#fff',
                                textShadow: '1px 1px 2px rgba(0,0,0,0.6)', // Text shadow for emphasis
                            }}
                        >
                            SkilHire
                        </Typography>
                    </Box>

                    {/* Hamburger Menu for Mobile */}
                    <IconButton
                        color="inherit"
                        edge="start"
                        aria-label="menu"
                        sx={{ display: { xs: 'block', md: 'none' } }}
                        onClick={handleDrawerToggle}
                    >
                        <MenuIcon />
                    </IconButton>

                    {/* Desktop Navigation Links */}
                    <Button
                        color="inherit"
                        component={Link}
                        to="/"
                        sx={{
                            display: { xs: 'none', md: 'block' },
                            '&:hover': {
                                backgroundColor: 'rgba(255, 255, 255, 0.2)',
                            },
                        }}
                    >
                        Home
                    </Button>
                    <Button
                        color="inherit"
                        component={Link}
                        to="/dashboard"
                        sx={{
                            display: { xs: 'none', md: 'block' },
                            '&:hover': {
                                backgroundColor: 'rgba(255, 255, 255, 0.2)',
                            },
                        }}
                    >
                        Opportunities
                    </Button>
                    <Button
                        color="inherit"
                        component={Link}
                        to="/profile"
                        sx={{
                            display: { xs: 'none', md: 'block' },
                            '&:hover': {
                                backgroundColor: 'rgba(255, 255, 255, 0.2)',
                            },
                        }}
                    >
                        Profile
                    </Button>
                    <Avatar
                        alt="Profile Photo"
                        src={profilePhoto}
                        sx={{
                            ml: 2,
                            display: { xs: 'none', md: 'block' },
                            transition: 'transform 0.3s ease',
                            '&:hover': {
                                transform: 'scale(1.1)',
                            },
                        }}
                    />
                </Toolbar>
            </AppBar>

            {/* Drawer for Mobile Navigation */}
            <Drawer
                anchor="left"
                open={mobileOpen}
                onClose={handleDrawerToggle}
                sx={{ display: { xs: 'block', md: 'none' } }}
            >
                {drawer}
            </Drawer>
        </>
    );
};

export default Navbar;
