import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Navbar from './components/Navbar';
import LandingPage from './pages/LandingPage';
import Dashboard from './pages/Dashboard';
import JobDetails from './pages/JobDetails';
import Profile from './pages/Profile';
import Login from './pages/Login';
import SignupPage from './pages/SignupPage';

function App() {
    const [profilePhoto, setProfilePhoto] = useState(null);

    const handleUpdateProfilePhoto = (newPhoto) => {
        setProfilePhoto(newPhoto); // Update the profile photo in the navbar
    };

    return (
        <Router>
            <Navbar profilePhoto={profilePhoto} />
            <Routes>
                <Route
                    path="/profile"
                    element={<ProfilePage onUpdateProfilePhoto={handleUpdateProfilePhoto} />}
                />
                {/* Other routes */}
            </Routes>
        </Router>
    );
    return (
        <Router>
            {/* Navbar should be conditionally rendered to exclude it on login and signup pages */}
            <Routes>
                {/* Default route redirects to login */}
                <Route path="/" element={<Navigate to="/login" />} />

                {/* Login and Signup Pages */}
                <Route path="/login" element={<Login />} />
                <Route path="/signup" element={<SignupPage />} />

                {/* Protected Routes */}
                <Route
                    path="*"
                    element={
                        <>
                            <Navbar />
                            <Routes>
                                <Route path="/landing" element={<LandingPage />} />
                                <Route path="/dashboard" element={<Dashboard />} />
                                <Route path="/jobs/:id" element={<JobDetails />} />
                                <Route path="/profile" element={<Profile />} />
                            </Routes>
                        </>
                    }
                />
            </Routes>
        </Router>
    );
}

export default App;
